function ProductCard() {
  const scrollContainerRef = useRef(null);

  const scrollLeft = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({
        left: -300,
        behavior: "smooth",
      });
    }
  };

  const scrollRight = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({
        left: 300,
        behavior: "smooth",
      });
    }
  };

  return (
    <div className="product-cards-container">
      <div className="scroll-controls">
        <Button
          variant="light"
          className="scroll-arrow left-arrow"
          onClick={scrollLeft}
        >
          <FaChevronLeft />
        </Button>

        <div className="horizontal-scroll-wrapper" ref={scrollContainerRef}>
          <Row className="flex-nowrap product-cards-row">
            {products.map((product) => (
              <Col key={product.id} className="product-card-col">
                <Link
                  to={`/product/${product.id}`}
                  state={{ product }}
                  style={{ textDecoration: "none", color: "inherit" }}
                >
                  <Card className="elegant-product-card">
                    {product.discount && (
                      <div className="sale-badge">-{product.discount}</div>
                    )}

                    <div className="product-image-container">
                      <Card.Img
                        variant="top"
                        src={product.image}
                        alt={product.name}
                      />
                      <div className="quick-view">Quick view</div>
                      {!product.inStock && (
                        <div className="out-of-stock">unavailable</div>
                      )}
                    </div>

                    <Card.Body>
                      <div className="product-category">{product.category}</div>
                      <Card.Title className="product-name">
                        {product.name}
                      </Card.Title>

                      <div className="product-rating">
                        <div className="stars">
                          {[...Array(5)].map((_, i) => (
                            <span
                              key={i}
                              className={
                                i < Math.floor(product.rating)
                                  ? "star-filled"
                                  : "star-empty"
                              }
                            >
                              ★
                            </span>
                          ))}
                        </div>
                        <span className="rating-value">{product.rating}</span>
                      </div>

                      <div className="product-price">
                        <span className="current-price">
                          {product.currentPrice}
                        </span>
                        {product.originalPrice && (
                          <span className="original-price">
                            {product.originalPrice}
                          </span>
                        )}
                      </div>

                      <button
                        className={`add-to-cart-btn ${
                          !product.inStock ? "disabled" : ""
                        }`}
                        disabled={!product.inStock}
                      >
                        {product.inStock ? "Add to Cart" : "Out of Stock"}
                      </button>
                    </Card.Body>
                  </Card>
                </Link>
              </Col>
            ))}
          </Row>
        </div>

        <Button
          variant="light"
          className="scroll-arrow right-arrow"
          onClick={scrollRight}
        >
          <FaChevronRight />
        </Button>
      </div>
    </div>
  );
}

export default ProductCard;
